mapper.insert:
insert
	into beer
	(
		  beer_no
		, beer_name
		, beer_ename
		, beer_style
		, beer_producer
		, beer_location
		, beer_alcohol
		, beer_container
		, origin_name
		, change_name
	)
	values
	(
		  seq_beer_no.nextval
		, #{beerName}
		, #{beerEname}
		, #{beerStyle}
		, #{beerProducer}
		, #{beerLocation}
		, #{alcohol}
		, #{beerContainer}
		, #{originName}
		, #{changeName}
	)

mapper.update:
update
	member
 set
 	point = point + #{point}
where
 	user_id = #{userId}

mapper.insert:
insert
	into notice
	(
		  notice_no
		, user_no
		, notice_title
		, notice_content
	)
	values
	(
		  seq_notice_no.nextval
		, #{userNo}
		, #{noticeTitle}
		, #{noticeContent}
	)

mapper.update:
update
	notice
 set
 	  notice_title = #{noticeTitle}
 	, notice_content = #{noticeContent}
 	, notice_date = sysdate
 where
 	notice_no = #{noticeNo}

mapper.select:
select count(*)
from member
where report > 4 and status = #{status}

select req_no, rfrom_no, req_content, req_status, req_num, req_cate_no, req_cate_title from report where req_status = #{status} and
rfrom_no = #{item}
order by req_no desc

mapper.update:
update
	report
   set
   	req_status = #{status}
 where
 	req_no = #{category}

mapper.select:
select count(*) from event
where status = #{category} and ev_title like '%'|| #{content} ||'%'
where status = #{category}

mapper.update:
