mapper.insert:
insert
	into beer
	(
		  beer_no
		, beer_name
		, beer_ename
		, beer_style
		, beer_producer
		, beer_location
		, beer_alcohol
		, beer_container
		, origin_name
		, change_name
	)
	values
	(
		  seq_beer_no.nextval
		, #{beerName}
		, #{beerEname}
		, #{beerStyle}
		, #{beerProducer}
		, #{beerLocation}
		, #{alcohol}
		, #{beerContainer}
		, #{originName}
		, #{changeName}
	)

