mapper.insert:
insert
	into notice
	(
		  notice_no
		, user_no
		, notice_title
		, notice_content
	)
	values
	(
		  seq_notice_no.nextval
		, #{userNo}
		, #{noticeTitle}
		, #{noticeContent}
	)

mapper.update:
update
	notice
 set
 	  notice_title = #{noticeTitle}
 	, notice_content = #{noticeContent}
 	, notice_date = sysdate
 where
 	notice_no = #{noticeNo}
