mapper.insert:
		insert
          into community
             (
                comm_no
              , comm_origin_src
              , comm_change_src
              , comm_content
              , comm_cate
              , user_no
             )
        values
             ( 
                seq_comm_no.nextval
              , #{ commOriginSrc }
              , #{ commChangeSrc }
              , #{ commContent }
              , #{ commCate }
              , (
		          select
		                 user_no
		            from member
		           where user_id = #{ userId }
		        )
             )

		insert
          into forum
             (
                for_no
              , for_origin_src
              , for_change_src
              , for_title
              , for_info
              , user_no
             )
        values
             ( 
                seq_comm_no.nextval
              , #{ forOriginSrc }
              , #{ forChangeSrc }
              , #{ forTitle }
              , #{ forInfo }
              , (
		          select
		                 user_no
		            from member
		           where user_id = #{ userId }
		        )
             )

mapper.update:
UPDATE MACSIM.FCOMMENTS t
   SET t.CO_CONTENT = #{coContent}
 WHERE t.CO_NO = #{coNo}

UPDATE MACSIM.FRECOMMENTS t
   SET t.RECO_CONTENT = #{coContent}
 WHERE t.RECO_NO = #{coNo}

update fcomments
   set co_status = 3
 where co_no = #{coNo}

update frecomments
   set reco_status = 3
 where reco_no = #{coNo}

mapper.select:
SELECT
       COUNT(*)
  FROM MACSIM.CLIKES
 WHERE CLIKES.COMM_NO = #{commNo}
   AND CLIKES.USER_NO = #{userNo}

mapper.insert:
insert
  into FCOMMENTS
     (
       CO_NO
     , FOR_NO
     , CO_CONTENT
     , USER_NO
     )
VALUES
     (
       seq_co_no.nextval
     , #{ forNo }
     , #{ coContent }
     , #{ userNo }
     )

insert
  into FRECOMMENTS
     (
       RECO_NO
     , CO_NO
     , RECO_CONTENT
     , USER_NO
     )
VALUES
     (
       seq_reco_no.nextval
     , #{ coNo }
     , #{ coContent }
     , (
          select
                 user_no
            from member
           where user_id = #{ userId }
        )
     )

mapper.update:
update forum
   set for_comment = for_comment + 1
 where for_no = #{ forNo }

update community
   set comm_origin_src = #{ commOriginSrc }
     , comm_change_src = #{ commChangeSrc }
     , comm_content = #{ commContent }
     , comm_cate = #{ commCate }
 where comm_no = #{ commNo }
   and user_no = #{ userNo }
