mapper.insert:
insert
  into gb_request
  (
    request_no
  , pno
  , user_no
  , req_status
  , address
  , gb_point
  )
  values
  (
    seq_request_no.nextval
  , #{pno}
  , #{userNo}
  , 'R'
  , #{address}
  , #{gbPoint}
  )

mapper.select:
	select
		   count(*)
      from gb_request
     where user_no = #{userNo}
       and pno = #{pno}
       and (req_status = 'R' or req_status= 'C')
